document.getElementById('imageSelector').addEventListener('change', function() {
    var selectedImage = this.value;
    var displayImage = document.getElementById('displayImage');

    if (selectedImage) {
        displayImage.src = selectedImage; // Set the image source to the selected option
        displayImage.style.display = 'block'; // Show the image
    } else {
        displayImage.src = ''; // Clear the image source
        displayImage.style.display = 'none'; // Hide the image
    }
});